mapper.insert:

 				INSERT INTO product(pname, pcnt,pprice,pcname,ptype,psort) VALUES(

 					#{pname},#{pcnt},#{pprice},

 					(
					SELECT cname 
						FROM category  WHERE cnum =#{categoryNumber}
					), (
					SELECT ctype 
						FROM category  WHERE cnum =#{categoryNumber}
					), (
					SELECT csort 
						FROM category  WHERE cnum =#{categoryNumber}
					)

 				)







						SELECT LAST_INSERT_ID() as pnum
